function oAnnotations = GetAnnotationsByField(p_oElan, p_sTierName, p_iFieldIndex, p_sFieldValue)%Zwraca adnotacje z warstwy o nazwie p_sTierName obiektu p_oElan,%które posiadaj¹ pole o nazwie p_sFieldValue na pozycji p_iFieldIndex.%%parametry:%%p_oElan - obiekt, na którego danych ma operowaæ funkcja. Parametr ten%musi byæ obiektem struktury oElan. W przeciwnym wypadku funkcja %zostaje przerwana z b³êdem.%%p_sTierName - nazwa warstwy, z której maj¹ zostaæ pobrane adnotacje. %Warstwa o danej nazwie musi istnieæ. Parametr ten musi byæ typu %string. W przeciwnym wypadku funkcja zostaje przerwana z b³êdem.%%p_iFieldIndex - indeks okreœlaj¹cy pozycjê pola, którego wartoœæ ma%byæ brana pod uwagê podczas wyszukiwania adnotacji. Parametr ten musi%byæ typu numerycznego. W przeciwnym wypadku funkcja zostaje przerwana%z b³êdem.%%p_sTierName - wartoœæ pola. Adnotacje, które posiadaj¹ pole o tej%wartoœci na okreœlonej pozycji spe³niaj¹ kryteria wyszukiwania. %Parametr ten musi byæ typu string. W przeciwnym wypadku funkcja %zostaje przerwana z b³êdem.%%wartoœæ zwracana:%%Tablica obiektów struktury oAnnotation.	if isstruct(p_oElan) == 0		oAnnotations = [];		disp('Error: p_oElan is not a structure');		return;	end		if ~isfield(p_oElan, 'oTiers') || ~isfield(p_oElan.oTiers, 'oAnnotations') || ~isfield(p_oElan.oTiers, 'sName')		oAnnotations = [];		disp('Error: p_oElan is a wrong structure');		return;	end		if ischar(p_sTierName) == 0 		oAnnotations = [];		disp('Error: p_sTierName is not a string.');		return;	end		if isnumeric(p_iFieldIndex) == 0 		oAnnotations = [];		disp('Error: p_iFieldIndex is not a number.');		return;	end		if ischar(p_sFieldValue) == 0 		oAnnotations = [];		disp('Error: p_sFieldValue is not a string.');		return;	end		bFound = false;	for i = 1 : numel(p_oElan.oTiers)		sTemp = p_oElan.oTiers(i).sName;		if strcmp(p_sTierName, sTemp)			oTempAnnotations = p_oElan.oTiers(i).oAnnotations;			bFound = true;		end	end		if ~bFound		oAnnotations = [];		disp('Error: Tier of such name does not exist.');		return;	end		if isempty(oTempAnnotations)		oAnnotations = [];		return;	end		bFound = false;	for i = 1 : numel(oTempAnnotations)		if p_iFieldIndex > numel(oTempAnnotations(i).sFields)			continue;		end				sTemp = oTempAnnotations(i).sFields(p_iFieldIndex);		if strcmp(p_sFieldValue, sTemp)			if exist('oAnnotations') == 0				oAnnotations = oTempAnnotations(i);			else				oAnnotations(end+1) = oTempAnnotations(i);			end			bFound = true;		end	end		if ~bFound		oAnnotations = [];		return;	end	end