function oAnnotations = GetAnnotationsByFrameRange(p_oElan, p_sTierName, p_iStartFrame, p_iStopFrame)%Zwraca adnotacje z warstwy o nazwie p_sTierName obiektu p_oElan, %które mieszcz¹ siê w zamkniêtym przedziale czasowym okreœlonym %parametrami p_iStartFrame i p_iStopFrame.%%parametry:%%p_oElan - obiekt, na którego danych ma operowaæ funkcja. Parametr %ten musi byæ obiektem struktury oElan. W przeciwnym wypadku funkcja %zostaje przerwana z b³êdem.%%p_sTierName - nazwa warstwy, z której maj¹ zostaæ pobrane adnotacje. %Warstwa o danej nazwie musi istnieæ. Parametr ten musi byæ typu %string. W przeciwnym wypadku funkcja zostaje przerwana z b³êdem.%%p_iStartFrame - pocz¹tek przedzia³u zamkniêtego wyra¿ony w ramkach,%z którego maj¹ zostaæ pobrane adnotacje. Parametr ten musi przyjmowaæ%wartoœæ wiêksz¹ ni¿ 0 i mniejsz¹ ni¿ p_iStopFrame. Musi byæ on typu%numerycznego. W przeciwnym wypadku funkcja zostaje przerwana%z b³êdem.%%p_iStopFrame - koniec przedzia³u zamkniêtego wyra¿ony w ramkach, z %którego maj¹ zostaæ pobrane adnotacje. Parametr ten musi przyjmowaæ%wartoœæ wiêksz¹ ni¿ 0 i wiêksz¹ ni¿ p_iStartFrame. Musi byæ on typu%numerycznego. W przeciwnym wypadku funkcja zostaje przerwana%z b³êdem.%%wartoœæ zwracana:%%Tablica obiektów struktury oAnnotation.	if isstruct(p_oElan) == 0		oAnnotations = [];		disp('Error: p_oElan is not a structure');		return;	end		if ~isfield(p_oElan, 'oTiers') || ~isfield(p_oElan.oTiers, 'oAnnotations') || ~isfield(p_oElan.oTiers, 'sName')		oAnnotations = [];		disp('Error: p_oElan is a wrong structure');		return;	end		if ischar(p_sTierName) == 0 		oAnnotations = [];		disp('Error: p_sTierName is not a string.');		return;	end		if isnumeric(p_iStartFrame) == 0 		oAnnotations = [];		disp('Error: p_iStartFrame is not a number.');		return;	end		if p_iStartFrame < 0 		oAnnotations = [];		disp('Error: p_iStartFrame is below zero.');		return;	end		if isnumeric(p_iStopFrame) == 0 		oAnnotations = [];		disp('Error: p_iStopFrame is not a number.');		return;	end		if p_iStopFrame < 0 		oAnnotations = [];		disp('Error: p_iStopFrame is below zero.');		return;	end		if p_iStartFrame > p_iStopFrame		oAnnotations = [];		disp('Error: p_iStartFrame is greater than p_iStopFrame.');		return;	end		bFound = false;	for i = 1 : numel(p_oElan.oTiers)		sTemp = p_oElan.oTiers(i).sName;		if strcmp(p_sTierName, sTemp)			oTempAnnotations = p_oElan.oTiers(i).oAnnotations;			bFound = true;		end	end		if ~bFound		oAnnotations = [];		disp('Error: Tier of such name does not exist.');		return;	end		if isempty(oTempAnnotations)		oAnnotations = [];		return;	end		bFound = false;	for i = 1 : numel(oTempAnnotations)			if (oTempAnnotations(i).iStartIndex >= p_iStartFrame) && (oTempAnnotations(i).iStopIndex <= p_iStopFrame)			if exist('oAnnotations') == 0				oAnnotations = oTempAnnotations(i);			else				oAnnotations(end+1) = oTempAnnotations(i);			end			bFound = true;		end	end		if ~bFound		oAnnotations = [];		return;	end	end